import React, {useEffect, useState, createContext, Navigate} from 'react';
import NavMenu from "./Components/NavMenu";
import ServiceCard from "./Components/ServiceCard/ServiceCard";
// import Image from "./Components/ServiceCard/banner_1.jpg";
import "./Styles/App.css";
// import CardPiece from "./Components/ServiceCard/ServiceCard";
import About from "./Components/About/About";
import Articles from "./Components/Articles/Articles";
// import Registration from "./Components/Registration/Registration";
// import Switch from "react-router-dom/es/Switch";
import Main from "./Components/Main/Main"
import {Route, BrowserRouter, Routes} from "react-router-dom";
import Registration from "./Components/Registration/Registration";
import Private from "./Components/Private/Private";

export const AuthContext = createContext(null);


function App() {


const [isAuth, setIsAuth] = useState(false)


function login() {
    localStorage.setItem('isAuth', 'true')
    setIsAuth(prevState => true)
}

function logout() {
    localStorage.removeItem('isAuth')
    setIsAuth(false)
}

function changeIsAuth() {
  setIsAuth(prevState => !prevState)
  console.log('Loged in');
}




const context = {isAuth, setIsAuth, login, logout, changeIsAuth}
  return (
      <AuthContext.Provider value={context}>
        <div className="App" >
                <NavMenu />
                <Route path="/private"> <Private isAuth={isAuth}/></Route>
                <Route path="/" exact={true} component={Main}/>
                <Route path="/articles" component={Articles}/>
                <Route path="/about" component={About}/>
                <Route path="/registration">
                  <Registration isAuth={isAuth}
                                changeIsAuth={changeIsAuth}/>
                </Route>
                <Route path="/services" component={ServiceCard}/>

        </div>
          </AuthContext.Provider>
  );
}

export default App;
