import './ServiceCard.css';
import React from "react";
import database from "../Database/Database";
// import Logo from '../../img/profession.png';
//
// const database2 = [
//     {
//         id: 1,
//         title: 'Консультация',
//         description: 'Описание консультации',
//         logo: Logo,
//         price: 1999
//     },
//     {
//         id: 2,
//         title: 'Составление резюме',
//         description: 'Описание услуги по составлению резюме',
//         logo: Logo,
//         price: 2999
//     },
// ]
function contentElem (props) {
    return (
        <li>{props.content}</li>
    );
}
function CardPiece(props) {
    return (
            <div className="card center">
                <div className="front">
                    <img className={"profLogo"} src ={props.logo} alt=" "/>
                    <h1>{props.title}</h1>
                </div>
                <div className="back">
                    <div className="back-content center">
                        <h2>{props.title}</h2>
                        <span>{props.description}</span>
                        <ul>


                            {props.content.map((serviceName) => {
                                return (<li>
                                    {serviceName.content}
                                </li>)
                            })}


                        </ul>
                        <span>{props.price}</span>
                    </div>
                </div>
            </div>
    );
}
function ServiceCard () {
    return (
    <div className="serviceContainer">
        {database.map((serviceName) => {
            return (<CardPiece key={serviceName.id}
                               title={serviceName.title}
                               description={serviceName.description}
                               logo={serviceName.logo}
                               price={serviceName.price}
                               content={serviceName.content}
            />)
        })}

    </div>
    );
}


export default ServiceCard;
