import React, { useState, useContext } from 'react';
import './Registration.css'
import ClientDataBase from '../Database/ClientDataBase'






function checklog() {
    const getUserList = ClientDataBase;
    getUserList.includes(login);
}

const Registration = ({isAuth, changeIsAuth}) => {
    const [login, setLogin] = useState('');

    function formSubmit (subm) {
        subm.preventDefault();

        if  (checklog) {
        return changeIsAuth();
        }
        else {
            return console.log('No such user')
        }
    }


    return (
        <form className="form" 
                noValidate
                onSubmit={formSubmit}
                >
            <h1 className="form__title">Регистрация</h1>
            <div className="form__email-wrapper">
                <input className="form__input-field" id="email" type="email" name="email_input"
                       placeholder="Введите адрес электронной почты" autoComplete="off"
                       value={login}
                       onChange={(event)=>setLogin(event.target.value)}/>
            </div>
                <div className="form__password-wrapper">
                    <input className="form__input-field" id="password" type="password" name="password_input"
                           placeholder="Введите пароль"/>
                </div>
            <button type='submit'>Войти</button>
            </form>
    );
};

export default Registration;
